import { Container, Box, Text } from "@chakra-ui/react"
import { Stack, HStack, VStack } from '@chakra-ui/react'
import axios from "axios";
import React, { useEffect, useState } from "react"

export default function Homepage() {

    const [todo, setTodo]=useState([]);
    const [inprogress, setInProgress]=useState([]);
    const [done, setDone]=useState([]);

    const [feedbacks, setFeedbacks] = useState([]);
    async function getData() {
        const result = await fetch("http://localhost:5000/tasks");
        const jresult = await result.json();
        setFeedbacks(jresult);
        //console.log(jresult.tasks);
        jresult.tasks.map(task => {
            if(task.status === 'To-Do'){
                todo=task.name;
            }
            if(task.status === 'In-Progress'){
                inprogress=task.name;
            }
            if(task.status === 'Done'){
                done=task.name;
            }
        })
    }
    useEffect(() => {
        getData();
    }, []);

    return (
        <Container maxW='xl' centerContent>
            <Box d='flex'
                p={3}
                w="100%"
                m="40px 0 15px 0"
                borderRadius="lg"
                borderWidth="ipx"
            >
                <Text fontSize='4xl' textAlign='center'>Kanban Application</Text>
            </Box>
            <Stack direction={['column', 'row']} spacing='24px'>
                <Box w='400px' h='600px' bg='#85aae3'>
                    To-Do
                    <ul>
                        <li>todo</li>
                    </ul>
                </Box>
                <Box w='400px' h='600px' bg='#65d9cf'>
                    In-progress
                    <ul>
                        <li>inprogress</li>
                    </ul>
                </Box>
                <Box w='400px' h='600px' bg='#4fdc95'>
                    Done
                    <ul>
                        <li>done</li>
                    </ul>
                </Box>
            </Stack>
        </Container>
    )
}
