import { Container, Box, Text } from "@chakra-ui/react"
import { Stack, HStack, VStack } from '@chakra-ui/react'
import axios from "axios";
import React, { useEffect, useState ,map} from "react"

export default function Homepage() {

    const [data, setData] = useState([]);
    async function getData() {
        const result = await fetch("http://localhost:5000/tasks");
        const jresult = await result.json();
        setData(jresult);
        //console.log(jresult.tasks);

    }
    useEffect(() => {
        getData();
      }, []);
    return(
       <>
        <div style={{"marginLeft":"7em","marginTop":"2em"}}>
        <div style={{"display":"inline-block","width":"30%","height":"500px","backgroundColor":"#edb407","marginRight":"2em","textAlign":"center"}}>ToDo
        {data?.map(
            ele=>(<>
                {(ele.status==="To-Do")?<Box fname={ele.name}/>:null}
                </>
            )
        )}
        </div>
        <div style={{"display":"inline-block","width":"30%","height":"500px","backgroundColor":"#077ed9","marginRight":"2em","textAlign":"center"}}>In Progress
        {data?.map(
            ele=>(<>
                {(ele.status==="In-Progress")?<Box fname={ele.name}/>:null}
                </>
            )
        )}      
        </div>
        <div style={{"display":"inline-block","width":"30%","height":"500px","backgroundColor":"#02c22c","marginRight":"2em","textAlign":"center"}}>Completed
        {data?.map(
            ele=>(<>
                {(ele.status==="Done")?<Box fname={ele.name}/>:null}
                </>
            )
        )}
        </div>
        </div>

       </>
    );
}
