import { Container, Box, Text } from "@chakra-ui/react"
import { Stack, HStack, VStack } from '@chakra-ui/react'
import axios from "axios";
import React, { useEffect, useState } from "react"
//import taskApi from "../api/tasks"

export const Homepage = () => {

    const [data, setData] = useState([]);
    useEffect(()=>{
        axios.get("http://localhost:3000/").then((responce)=>{
            setData(responce.data)
            console.log(data);
        }).catch((err)=>{
            alert(err);
        })
    },[])
    /*const getToDoSection = async () => {
        try {
            const section = await taskApi.get();
            setData([...data, section])
        } catch (error) {
            alert(error)
        }
    }*/


    return (
        <Container maxW='xl' centerContent>
            <Box d='flex'
                p={3}
                w="100%"
                m="40px 0 15px 0"
                borderRadius="lg"
                borderWidth="ipx"
            >
                <Text fontSize='4xl' textAlign='center'>Task Kanban</Text>
            </Box>
            <Stack direction={['column','row']} spacing='24px'>
                <Box w='400px' h='600px' bg='#85aae3'>
                    To-Do
                </Box>
                <Box w='400px' h='600px' bg='#65d9cf'>
                    Pending
                </Box>
                <Box w='400px' h='600px' bg='#4fdc95'>
                    Completed
                </Box>
            </Stack>
        </Container>
    )
}
